  /** Number of admissions in VERIFIED status (documents approved) */
  verified?: number;
  /** Number of admissions in REJECTED status */
  rejected?: number;
  /** Number of admissions where fee has been paid */
  feePaid: number;
  /** Number of admissions that have been finalized (enrollment complete) */
